from vpython import *

scene = canvas(title='Gravity Field and Electric Field', width=800, height=600)

# Create gravitational field arrows for a single mass
G = 6.67430e-11  # gravitational constant
M = 5.972e24  # mass of Earth in kg
R = 6.371e6  # radius of Earth in meters

def gravity_field(pos):
    r = mag(pos)
    F = -G * M / r**2
    return F * norm(pos)

earth = sphere(pos=vector(0, 0, 0), radius=R, color=color.blue)

for x in range(-10, 11):
    for y in range(-10, 11):
        for z in range(-10, 11):
            pos = vector(x*2*R, y*2*R, z*2*R)
            field = arrow(pos=pos, axis=gravity_field(pos), color=color.red)

# Create electric field arrows for a single charge
k = 8.9875517923e9  # Coulomb constant
q = 1.602e-19  # charge of an electron in C
r_charge = 1e-10  # radius of charge in meters

def electric_field(pos):
    r = mag(pos)
    F = k * q / r**2
    return F * norm(pos)

charge = sphere(pos=vector(5*R, 0, 0), radius=r_charge, color=color.yellow)

for x in range(-10, 11):
    for y in range(-10, 11):
        for z in range(-10, 11):
            pos = vector(x*2*R, y*2*R, z*2*R)
            field = arrow(pos=pos, axis=electric_field(pos), color=color.green)
